    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in!{% endblock %}


{% block body %}
<center>
<form method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    <br />

    {% if app.user %}
        <div class=\"mb-3\">
            Vous êtes connecté en tant que {{ app.user.prenom }}, <a href=\"{{ path('app_logout') }}\">Se déconnecter</a>
        </div>
    {% endif %}
    
    <h1 class=\"h3 mb-3 font-weight-normal centrer\">Merci de vous connecter pour accéder au site.</h1>
   
    <br />
    <br />
    <br />
    <br />
    <br />
    
    <div class=\"form-group\">
    <label for=\"inputEmail\">Email</label>
    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control col-sm-3\" required autofocus>
    </div>
   
    <br />
    
    <div class=\"form-group\">
    <label for=\"inputPassword\">Mot de passse</label>
    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control col-sm-3\"  required>
    </div>
    


    <input type=\"hidden\" name=\"_csrf_token\"
           value=\"{{ csrf_token('authenticate') }}\"
    >

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}
    <br />
    <button class=\"btn btn-lg btn-primary\" type=\"submit\">
        Se connecter
    </button>

</form>
</center>
{% endblock %}
", "security/login.html.twig", "C:\\Users\\SIO1\\Documents\\Symfony\\TimeTimer1.1\\templates\\security\\login.html.twig");
